import axios from 'axios'

const API_URL = "http://localhost:8090/api/v1/persons";

class PersonServices {
    getAllPersons(){
        return axios.get(API_URL);

    }
    addPerson(person){
        return axios.post(API_URL, person);
    }
}

export default new PersonServices()
